getVariantWithWidth.Source:
app.getVariantWithWidth = function( variants, width ) {

		var dx = Infinity;
		var nearest = false;

		for( var s = 0; s < variants.length; s++ ) {
			var variant = variants[ s ];
			var candidateDx = Math.abs( variant.width - width );
			if( candidateDx < dx ) {
				dx = candidateDx;
				nearest = s;
			}
		}
		return variants[ nearest ];
	}

