		}

		return false;
	}

getDurationString.Source:
app.getDurationString = function( duration ) {

		if( !duration ) {
			return "";
		}

		var minutes = Math.floor( duration / 60 );
		var seconds = duration % 60;
		// TO-TRANSLATE
		var min = " min ", sec = " sec";

		var durationString = "";

		if( minutes ) {
			durationString += ( minutes + min );
		}

		if( seconds ) {
			durationString += ( seconds + sec );
		}

		return durationString;

	}

