setListeners.Source:
app.mainNavigation.prototype.setListeners = function() {

		var self = this;

		window.addEventListener( 'scroll', function() {

			var scroll = window.scrollY;
			if (scroll >= self.bottom) {
				self.navElement.classList.add( "fixed" );
			} else {
				self.navElement.classList.remove( "fixed" );
			}
		} );

		app.listenForMultiple( this.mainMoreLinksButton, [ 'keypress', 'click' ], function() {

			this.classList.toggle( "open" );
		} );

		this.mobileMenuExpander.addEventListener( 'click', function() {

			document.querySelector( '.masthead' ).classList.toggle( "open" );
		} );

		app.listenForMultiple( this.mainSearchButton, [ 'keypress', 'click' ], function() {
			self.searchElement.classList.toggle( 'active' );
			self.searchBox.focus();
		} );

		app.listenForMultiple( this.subNavMore, [ 'keypress', 'click' ], function() {

			self.subNav.classList.toggle( 'open' );
		} );

	}

