getMatchDays.Source:
app.fixtureNavigation.prototype.getMatchDays = function( fixtures ) {

		var matchDays = [];

		for( var i = 0; i < fixtures.content.length; i++ ) {
			var match = fixtures.content[ i ];
			var day = this.dayIndexMap[ new Date( match.kickoff.millis ).getDay() ];

			if( !matchDays[ day ] ) {
				matchDays[ day ] = {};
				matchDays[ day ].matches = [];
				matchDays[ day ].index = day;
			}

			if( match.status === "L" ) {
				this.live = true;
			}

			matchDays[ day ].matches.push( match );
		}

		return matchDays;

	}

