request.Source:
core.request = function( url, method, callback, target, data, headers, type )Â {

		var config = {
			url: url,
			method: method || "GET",
			target: target,
			callback: callback,
			data: data || {},
			headers: headers || [], 
			type: type || "json"
		};

		var xhttp = new XMLHttpRequest();
		xhttp.open( config.method, config.url, true );
		xhttp.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded; charset=UTF-8');
		config.headers.map( function( header ){
			xhttp.setRequestHeader( header.label, header.value );
		} );
		xhttp.data = config.data;
		xhttp.responseType = config.type;
		xhttp.onreadystatechange = function() {
			if ( this.readyState === 4 ) {
				if( this.status >= 200 && this.status < 400 ) {
					config.callback.call( config.target, this.response, config );
				}
				else {
					config.callback.call( config.target, this.status, this.statusText, config );
				}
			}

		};
		xhttp.send();
		xhttp = null;

	}

