var gulp = require('gulp');
var concat = require('gulp-concat');

gulp.task('default', function() {

	return gulp.src([
		'./core/core.js',
		'./core/utils/**/*.js',
		'./core/appBuilder/**/*.js',
		'./parser/plugins/**/*.js',
		'./parser/parser.js',
		'./core/exports.js'
	])
		.pipe(concat('index.js'))
		.pipe(gulp.dest('../'));
});